import "./styles.css";
import Net from "./Net";
import { useContext, useEffect, useState } from "react";
import { context, initialState } from "./context";

export default function App() {
  const x = 20;
  const y = 50;

  const [state, setState] = useState(initialState);

  const store = useContext(context);

  // useEffect(() => {
  //   setTimeout(() => {
  //     const randomI = Math.floor(Math.random() * x);
  //     const randomJ = Math.floor(Math.random() * y);

  //     setState({
  //       ...state,
  //       winner: [randomI, randomJ],
  //     });
  //   }, 10000);
  // });

  const restart = () => {
    setState(initialState);
  };

  const jackBot = () => {
    const randomI = Math.floor(Math.random() * x);
    const randomJ = Math.floor(Math.random() * y);

    setState({
      ...state,
      winner: [randomI, randomJ],
    });
  };

  return (
    <context.Provider value={{ state, setState }}>
      <div className="App">
        <h1>Your Credit {state.credit.toFixed(2)} PLN</h1>
        <h2>Bullet cost 0.10 PLN, Hit makes {x * y}x = 100 PLN</h2>
        <button onClick={restart}>restart</button>
        <button onClick={jackBot}>open</button>
        <Net x={x} y={y} />
      </div>
    </context.Provider>
  );
}
