import { useState } from "react";

export default function Cell() {
  let styles = {
    background: "red",

    border: "1px solid black",
  };

  const [power, setPower] = useState(0);

  const classes = ["cell"];

  if (power) [classes.push("on")];

  return (
    <div
      className={classes.join(" ")}
      style={styles}
      onClick={() => setPower(1)}
    ></div>
  );
}
