import Cell from "./Cell";

import useAudio from "./useAudio";
const audioUrl = "http://www.ne.jp/asahi/music/myuu/wave/hana.mp3";

const Net = ({ x, y }: any) => {
  let styles = {
    border: "3px solid green",
    padding: "5px",
    justifyContent: "center",
    display: "grid",
    gridTemplateColumns: `repeat(${x},20px)`,
    gridTemplateRows: `repeat(${y},20px)`,
  };

  const [playing, currentTime, play, pause, jump] = useAudio(audioUrl);

  let cells = [];
  for (let i = 0; i < x; i++) {
    for (let j = 0; j < y; j++) {
      cells.push(<Cell key={Math.random()} />);
    }
  }

  const handleClick = () => {
    play;
  };

  return (
    <>
      <p>currenttime: {currentTime}</p>
      <button onClick={playing ? pause : play}>
        {playing ? "Pause" : "Play"}
      </button>
      <div className="net" style={styles}>
        {cells}
      </div>
    </>
  );
};

export default Net;
