  const [power, setPower] = useState(0);

  const classes = ["cell"];

  if (power) [classes.push("on")];

  const handleClick = () => {
    const w: any = window;
    w.play();

    if (!power) {
      // remove credit
      console.log(
        "ðŸš€ ~ file: Cell.tsx ~ line 18 ~ handleClick ~ credit",
        credit.setState(credit.state - 1)
      );
      // credit.setState(credit - 1);
      // add cell to context
    }
    setPower(1);
  };

  return (
    <div
      className={classes.join(" ")}
      style={styles}
      onClick={handleClick}
    ></div>
  );
};

export default Cell;
