import { useContext, useState } from "react";
import { context } from "./App";

const Cell = ({ i, j }: any) => {
  let styles = {};
  const myContextVal = useContext(context);

  const [power, setPower] = useState(0);

  const classes = ["cell"];

  if (power) [classes.push("on")];

  const handleClick = () => {
    if (power) {
      // remove credit
      myContextVal.setState(myContextVal.state + 1);

      // add cell to context
    }
    setPower(1);
  };

  return (
    <div
      className={classes.join(" ")}
      style={styles}
      onClick={handleClick}
    ></div>
  );
};

export default Cell;
