import ringer from "./assets/shooting.mp3";

const Net = ({ x, y }) => {
  let styles = {
    border: "3px solid green",
    padding: "5px",
    justifyContent: "center",
    display: "grid",
    gridTemplateColumns: `repeat(${x},20px)`,
    gridTemplateRows: `repeat(${y},20px)`,
  };

  const audio = new Audio(ringer);

  let cells = [];
  for (let i = 0; i < x; i++) {
    for (let j = 0; j < y; j++) {
      cells.push(<Cell key={Math.random()} />);
    }
  }

  const handleClick = () => {
    // consol;
  };

  return (
    <>
      <div
        className="net"
        style={styles}
        onClick={() => {
          audio.loop = true;
          audio.play();
        }}
      >
        {cells}
      </div>
    </>
  );
};

